    @Test
    public void getLoginResultFail() throws Exception {

        URL url = new URL("http://192.168.56.1:8080/user/login");

        LoginRequest loginRequest = new LoginRequest("Inoh", "dlsdh12");
        LoginResult loginResult = new ServerProxy().getLoginResult(url, loginRequest);

        assertNotNull(loginResult);
        assertNull(loginResult.getAuthToken());
        assertNull(loginResult.getPersonID());
        assertNull(loginResult.getUserName());
        assertNotNull(loginResult.getMessage());
        assertTrue(loginResult.getMessage().contains("error"));
        assertTrue(loginResult.getMessage().contains("password"));
    }

    @Test
    public void getRegisterResultFail() throws Exception {
        //Register the user with the taken username
        URL url = new URL("http://192.168.56.1:8080/user/register");
        RegisterRequest registerRequest = new RegisterRequest("hello2", "hello123", "hello@gmail.com", "He", "llo", "f");
        RegisterResult registerResult = new ServerProxy().getRegisterResult(url, registerRequest);

        assertNotNull(registerResult);
        assertNull(registerResult.getUserName());
        assertNull(registerResult.getPersonID());
        assertNull(registerResult.getAuthToken());
        assertNotNull(registerResult.getMessage());
        assertTrue(registerResult.getMessage().contains("error"));
        assertTrue(registerResult.getMessage().contains("username already taken"));
    }

    @Test
    public void getAllPeopleFail() throws Exception {

        ServerProxy serverProxy = new ServerProxy();
        LoginRequest loginRequest = new LoginRequest("Inoh", "dlsdh123");
        URL url = new URL("http://192.168.56.1:8080/user/login");
        LoginResult loginResult = serverProxy.getLoginResult(url, loginRequest);
        String authToken = loginResult.getAuthToken();
        authToken += "a";

        URL url2 = new URL("http://192.168.56.1:8080/user/person");
        PersonResult personResult = serverProxy.getAllPeopleResult(url2, authToken);

        assertNotNull(personResult);
        assertNotNull(personResult.getMessage());
        assertNull(personResult.getData());
        assertTrue(personResult.getMessage().contains("error"));
        assertTrue(personResult.getMessage().contains("invalid token"));
    }

